import React, { Fragment } from 'react';
import Axios from 'axios';
class Body extends React.Component {

    constructor() {
        super();
        this.state = {
            city: '',
            input: {},
            error: '',
            showWeather: false,
            fadeIn:false
        }
    }

    getCity = (e) => {
        this.setState({ city: e.target.value }, () => {
            console.log(this.state.city);
        });
    }


    search = () => {
        const apiUrl = "https://api.openweathermap.org/data/2.5/weather?&units=metric&q=";
        const apiKey = "b067b3b01460c91047ad00711366af60";

        if (this.state.city.trim !== '') {
            Axios.get(apiUrl + this.state.city + `&appid=${apiKey}`).then(response => {
                this.setState({ input: response.data, error: '', showWeather: true ,fadeIn:true}, () => {
                    console.log(this.state.input);
                });


            })
                .catch(error => {
                    console.log("Error fetching data: ", error);
                    this.setState({ input: {}, error: "Invalid city name!.", showWeather: false ,fadeIn:false})
                })
        }
        else {
            this.setState({ input: {}, error: "Please enter a city name.", showWeather: false,fadeIn:false })
        }


    }




    render() {
        return (
            <div className={`card ${this.state.fadeIn? "slide-down":""}`}>
                <div className="search">
                    <input type="text" placeholder="Enter city name" autoFocus onBlur={this.getCity} />
                    <button onClick={this.search}><img src="images/search.png" /></button>
                </div>
                <div className="error">{this.state.error}</div>
                {this.state.showWeather && (
                    <div className={`weather ${this.state.fadeIn? "fade-in":""}`}>
                        {
                            <Fragment>
                                <div><img src="images/clear.png" className="weather-icon" /></div>
                                <div className="temp">{this.state.input.main.temp}</div>
                                <div className="city">{this.state.city}</div>
                                <div className="weather-details">
                                    <div className="col">
                                        <img src="images/humidity.png" />
                                        <div>
                                            <p className="humidity">{this.state.input.main.humidity}</p>
                                            <p>Humidity</p>
                                        </div>
                                    </div>
                                    <div className="col">
                                        <img src="images/wind.png" />
                                        <div>
                                            <p className="wind">{this.state.input.wind.speed}</p>
                                            <p>Wind speed</p>
                                        </div>
                                    </div>
                                </div>
                            </Fragment>
                        }
                    </div>

                )}



            </div>


        )
    }

}

export default Body;
