import React from 'react';
import Axios from 'axios';
class Body extends React.Component{

constructor(){
    super();
    this.state={
        textContent:'',
        city:'',
        input:{}
    }
}

componentDidMount(){
    const apiUrl = "https://api.openweathermap.org/data/2.5/weather?&units=metric&q=";
    const apiKey = "b067b3b01460c91047ad00711366af60"; 
    Axios.get(apiUrl + this.state.city + `&appid=${apiKey}`).then(response=>{
        this.setState({input: response.data});
        console.log(this.state.input);
    })
}

getCity(e){
    this.setState({textContent:e.target.value})
}

search(){
    this.setState({city:this.state.textContent})
}




render(){
    return(
<div className="card">
        <div className="search">
            <input type="text" placeholder="Enter city name" autoFocus onChange={this.getCity} />
            <button onClick={this.search}><img src="images/search.png"/></button>
        </div>
        <div className="error">Invalid city name</div>
        <div className="weather">
            <div><img src="images/clear.png" className="weather-icon"/></div>
            <div className="temp">26.7Â°c</div>
            <div className="city">Bangalore</div>
            <div className="weather-details">
                <div className="col">
                    <img src="images/humidity.png"/>
                    <div>
                        <p className = "humidity">31%</p>
                        <p>Humidity</p>
                    </div>
                </div>
                <div className="col">
                    <img src="images/wind.png"/>
                    <div>
                        <p className ="wind">31%</p>
                        <p>Wind speed</p>
                    </div>
                </div>
            </div>
        </div>
    </div>


    )
}

}

export default Body;
