import React, { useState } from 'react';
import Axios from 'axios';

const Cardnew = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);

  const getWeatherIconSrc = (weatherCondition) => {
    switch (weatherCondition) {
        case 'Clouds':
          return 'images/clouds.png';
        case 'Clear':
          return 'images/clear.png';
        case 'Rain':
          return 'images/rain.png';
        case 'Drizzle':
          return 'images/drizzle.png';
        case 'Mist':
          return 'images/mist.png';
          case 'snow':
              return 'images/snow.png';
        default:
          return 'images/clear.png';
      }
  };

  const search = () => {
    const apiUrl = "https://api.openweathermap.org/data/2.5/weather?&units=metric&q=";
    const apiKey = "b067b3b01460c91047ad00711366af60";

    if (city.trim() !== '') {
      Axios.get(`${apiUrl}${city}&appid=${apiKey}`)
        .then(response => {
          setWeatherData(response.data);
          setError(null);
        })
        .catch(error => {
          console.log("Error fetching data: ", error);
          setWeatherData(null);
          setError("Invalid city name!");
        });
    } else {
      setError("Please enter a city name.");
      setWeatherData(null);
    }
  };

  return (
    <div className={`card ${error == null? "slide-down":""}`}>
      <div className="search">
        <input
          type="text"
          placeholder="Enter city name"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button onClick={search}>Search</button>
      </div>

      {error && <div className="error">{error}</div>}

      {weatherData && (
        <div className="weather-details">
          <img src={getWeatherIconSrc(weatherData.weather[0].main)} alt="Weather Icon" />
          <div className="temp">{weatherData.main.temp}</div>
          <div className="city">{city}</div>
          <div className="weather-info">
            <p>Humidity: {weatherData.main.humidity}</p>
            <p>Wind Speed: {weatherData.wind.speed}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cardnew;
